CREATE TABLE `teste`.`locais` ( `id` INT(10) NOT NULL AUTO_INCREMENT , `nome` VARCHAR(100) NOT NULL , `cep` VARCHAR(8) NOT NULL , `logradouro` VARCHAR(150) NOT NULL , `complemento` VARCHAR(100) NOT NULL , `numero` VARCHAR(100) NOT NULL , `bairro` VARCHAR(100) NOT NULL , `uf` VARCHAR(2) NOT NULL , `cidade` VARCHAR(100) NOT NULL , `data` DATE NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;